        public int SovlePuzzle1()
        {
            return _input.Select(x => Floor(x)).Sum();
        }

        public int SolvePuzzle2()
        {
            int[] arr = _input.Select(x => Floor(x)).ToArray();
            int sum = 0;
            for (int i = 0; i < arr.Length; i++)
            {
                sum += arr[i];
                if (sum < 0)
                    return i + 1;
            }

            return 0;
        }

        private int Floor(char input)
        {
            switch (input)
            {
                case '(':
                    return 1;
                case ')':
                    return -1;
                default:
                    throw new NotSupportedException();
            }
        }
